#include <string>
#include <vector>
using namespace std;

class Solution {
public:
    string shortestPalindrome(string s) {
		// printf("start: %d\n", s.size()-1);
		int i; // 对称轴*2
		for (i = s.size()-1; i > 0; i--) {
			// printf("i:%d\n", i);
			int j;
			for (j = (i-1)/2; j >= 0; j--) { // 依次j和i-j对称，发现不等时break
				if (s[j] != s[i - j]) {
					// 如果j和i-j不相等，则向j前面找到k和i-j相等
					int k;
					for (k = j-1; k >= 0; k--) {
						if (s[k] == s[i-j]) {
							break;
						}
					}
					// 最坏情况，k是-1，即没有和k相同的，需要增加一项
					i = i-j+k+1; // k和i-j的对称轴是i-j+k
					// printf("new i:%d\n", i);
					break;
				}
			}
			if (j < 0) break; // 如果完全对称
		}
		// printf("center: %d\n", i);
		return complete(s, i);
	}

	// 针对c作为中心，进行补齐。对称轴坐标放大2倍
	string complete(const string& s, int c) {
		if (s.size() <= 1 || c < 0 || c > s.size()-1) return s;
		string r(s.crbegin(), s.crbegin()+(s.size()-1-c));
		return r + s;
	}
};

int main() {
	// string str("aacecaaa");
	string str("");
	// string str("abca");
	// string str("abcd");
	// printf("input: %s\n", str.data());
	if (str.size() < 20) printf("input : %s\n", str.data());
	Solution s;
	string output = s.shortestPalindrome(str);
	if (output.size() < 20) printf("output: %s\n", output.data());
	return 0;
}
